SELECT L.LV HOUR, NVL(O.COUNT, 0) COUNT FROM (SELECT EXTRACT(HOUR FROM CAST(DATETIME AS TIMESTAMP)) HOUR, COUNT(*) COUNT FROM ANIMAL_OUTS GROUP BY EXTRACT(HOUR FROM CAST(DATETIME AS TIMESTAMP)) ORDER BY HOUR) O, (SELECT (LEVEL-1) LV FROM DUAL CONNECT BY LEVEL <= 24) L WHERE L.LV = O.HOUR(+) ORDER BY L.LV;